// src/components/MapView.js

import React, { useEffect, useState, useCallback } from 'react';
import mapboxgl from 'mapbox-gl';
import Papa from 'papaparse';
import { Bar, Pie, Doughnut, PolarArea } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  RadialLinearScale,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import { CircularProgressbar } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

import styles from './styles'; // Ensure this file exists and is properly configured
import preprocessData from './preprocessData'; // Ensure this file exists and is properly configured
import { MAPBOX_TOKEN } from './constants'; // Ensure this file contains your Mapbox token
import blueMarkerIcon from '../assets/images/custom-marker-blue.png'; // Ensure these assets exist
import redMarkerIcon from '../assets/images/custom-marker-red.png';
import './marker.css'; // Ensure this CSS file exists for marker styling

// Register Chart.js components
ChartJS.register(
  RadialLinearScale,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  ChartDataLabels
);

// Set Mapbox access token
mapboxgl.accessToken = MAPBOX_TOKEN;

const MapView = () => {
  // State Variables
  const [map, setMap] = useState(null);
  const [geoData, setGeoData] = useState([]);
  const [numericHeaders, setNumericHeaders] = useState([]);
  const [stringHeaders, setStringHeaders] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [chartType, setChartType] = useState('Bar');
  const [chartData, setChartData] = useState(null);
  const [totalRows, setTotalRows] = useState(0);
  const [totalColumns, setTotalColumns] = useState(0);
  const [markers, setMarkers] = useState([]);
  const [progress, setProgress] = useState({
    upload: 0,
    preprocess: 0,
    geocode: 0,
  });
  const [progressMessages, setProgressMessages] = useState({
    upload: '',
    preprocess: '',
    geocode: '',
  });
  const [stateList, setStateList] = useState([]);
  const [selectedState1, setSelectedState1] = useState('');
  const [selectedState2, setSelectedState2] = useState('');
  const [locationColumn, setLocationColumn] = useState('');
  const [markersAdded, setMarkersAdded] = useState(false);
  const [dataNeedsGeocoding, setDataNeedsGeocoding] = useState(true);
  const [compareMarkersEnabled, setCompareMarkersEnabled] = useState(false);
  const [stateCoordinates, setStateCoordinates] = useState({}); // To store state centroids for markers

  // Initialize Map
  useEffect(() => {
    const initializeMap = new mapboxgl.Map({
      container: 'map', // HTML container id
      style: 'mapbox://styles/mapbox/streets-v11', // style URL
      center: [-98.5795, 39.8283], // starting position [lng, lat] (center of USA)
      zoom: 3, // starting zoom
    });

    // Add navigation control (the +/- zoom buttons)
    const nav = new mapboxgl.NavigationControl();
    initializeMap.addControl(nav, 'top-right');

    setMap(initializeMap);

    // Clean up on unmount
    return () => initializeMap.remove();
  }, []);

  // Reverse Geocode Function to get State Name
  const reverseGeocodeState = useCallback(async (latitude, longitude) => {
    try {
      const response = await fetch(
        `https://api.mapbox.com/geocoding/v5/mapbox.places/${longitude},${latitude}.json?types=region&access_token=${MAPBOX_TOKEN}`
      );
      const data = await response.json();
      if (data.features && data.features.length > 0) {
        return data.features[0].text; // Returns the state name
      } else {
        return null;
      }
    } catch (error) {
      console.error('Reverse geocoding error:', error);
      return null;
    }
  }, []);

  // Preprocess Data Function
  const handlePreprocess = useCallback(
    async (rawData) => {
      setProgress((prev) => ({ ...prev, preprocess: 0 }));

      // Remove null, undefined, and inconsistent data rows
      const cleanRawData = rawData.filter(
        (item) => item !== null && item !== undefined && Object.keys(item).length > 0
      );

      // Determine if data contains latitude and longitude
      const dataContainsLatLng = cleanRawData.some(
        (item) => item.latitude && item.longitude
      );

      setDataNeedsGeocoding(!dataContainsLatLng);

      // Determine the location column dynamically
      const possibleLocationColumns = [
        'State',
        'state',
        'Province',
        'province',
        'City',
        'city',
      ];
      let detectedLocationColumn = possibleLocationColumns.find(
        (col) => cleanRawData[0] && cleanRawData[0].hasOwnProperty(col)
      );

      setLocationColumn(detectedLocationColumn || 'state');

      console.log('Detected Location Column:', detectedLocationColumn);

      // Preprocess data
      const processedData = await preprocessData(
        cleanRawData,
        setProgress,
        detectedLocationColumn
      );

      if (!processedData || processedData.length === 0) {
        alert('No valid data after preprocessing.');
        return;
      }

      setProgress((prev) => ({ ...prev, preprocess: 50 }));

      // Initialize state data aggregation
      const stateDataMap = {};
      const stateCoordsMap = {}; // To store state coordinates
      const numericFields = new Set();

      if (dataContainsLatLng) {
        // Data contains latitude and longitude, need to reverse geocode to get state names
        for (const item of processedData) {
          const latitude = parseFloat(item.latitude);
          const longitude = parseFloat(item.longitude);
          if (!isNaN(latitude) && !isNaN(longitude)) {
            let stateName = await reverseGeocodeState(latitude, longitude);
            if (!stateName) continue; // Skip if state name couldn't be retrieved

            // Aggregate data per state
            if (!stateDataMap[stateName]) {
              stateDataMap[stateName] = { ...item };
              stateDataMap[stateName].state = stateName;
              stateDataMap[stateName].count = 1;
              stateCoordsMap[stateName] = { latitude, longitude };
            } else {
              // Sum numeric fields
              Object.keys(item).forEach((key) => {
                const value = parseFloat(item[key]);
                if (!isNaN(value)) {
                  numericFields.add(key);
                  stateDataMap[stateName][key] =
                    (stateDataMap[stateName][key] || 0) + value;
                }
              });
              stateDataMap[stateName].count += 1;
            }
          }
        }
      } else if (detectedLocationColumn) {
        // Data contains state or city names
        for (const item of processedData) {
          let stateName = item[detectedLocationColumn];

          if (detectedLocationColumn.toLowerCase().includes('city')) {
            // If the location column is a city, we need to get the state name
            const coords = await geocodeLocation(stateName);
            if (coords.latitude && coords.longitude) {
              stateName = await reverseGeocodeState(coords.latitude, coords.longitude);
              if (!stateName) continue; // Skip if state name couldn't be retrieved
            } else {
              continue;
            }
          }

          if (!stateName) continue;

          // Aggregate data per state
          if (!stateDataMap[stateName]) {
            stateDataMap[stateName] = { ...item };
            stateDataMap[stateName].state = stateName;
            stateDataMap[stateName].count = 1;
          } else {
            // Sum numeric fields
            Object.keys(item).forEach((key) => {
              const value = parseFloat(item[key]);
              if (!isNaN(value)) {
                numericFields.add(key);
                stateDataMap[stateName][key] =
                  (stateDataMap[stateName][key] || 0) + value;
              }
            });
            stateDataMap[stateName].count += 1;
          }
        }

        // Geocode states to get their coordinates
        for (const stateName of Object.keys(stateDataMap)) {
          const response = await fetch(
            `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(
              stateName
            )}.json?types=region&access_token=${MAPBOX_TOKEN}`
          );
          const data = await response.json();
          if (data.features && data.features.length > 0) {
            const [longitude, latitude] = data.features[0].center;
            stateCoordsMap[stateName] = { latitude, longitude };
          }
        }
      } else {
        alert('No location information found in the dataset.');
        return;
      }

      setProgress((prev) => ({ ...prev, preprocess: 80 }));

      // Calculate averages for numeric fields
      const aggregatedData = Object.values(stateDataMap).map((item) => {
        const count = item.count || 1;
        numericFields.forEach((key) => {
          const parsedValue = parseFloat(item[key]);
          if (!isNaN(parsedValue)) {
            item[key] = parsedValue / count;
          } else {
            item[key] = 0; // Assign 0 if value is invalid
          }
        });
        return item;
      });

      setGeoData(aggregatedData);
      setStateCoordinates(stateCoordsMap);

      // Identify numeric and string headers
      if (aggregatedData.length > 0) {
        const headers = Object.keys(aggregatedData[0]);
        const numeric = [];
        const strings = [];

        headers.forEach((header) => {
          if (
            [
              'latitude',
              'longitude',
              'GeoLocation',
              'locationID',
              'count',
              'state',
            ].includes(header)
          )
            return;

          const isNumeric = aggregatedData.every((item) => {
            const value = parseFloat(item[header]);
            return !isNaN(value);
          });
          if (isNumeric) {
            numeric.push(header);
          } else {
            strings.push(header);
          }
        });

        setNumericHeaders(numeric);
        setStringHeaders(strings);

        setTotalRows(aggregatedData.length);
        setTotalColumns(headers.length);

        // Extract unique states for comparison
        const uniqueStates = Object.keys(stateDataMap);
        setStateList(uniqueStates);
      } else {
        alert('No data available after filtering.');
      }

      setProgress((prev) => ({ ...prev, preprocess: 100 }));

      // Reset markersAdded state when new data is uploaded
      setMarkersAdded(false);
    },
    [reverseGeocodeState]
  );

  // Geocode Function to get coordinates from location name
  const geocodeLocation = async (locationName) => {
    if (!locationName) return { latitude: null, longitude: null };
    try {
      const response = await fetch(
        `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(
          locationName
        )}.json?access_token=${MAPBOX_TOKEN}`
      );
      const data = await response.json();
      if (data.features && data.features.length > 0) {
        const [longitude, latitude] = data.features[0].center;
        return { latitude, longitude };
      }
      return { latitude: null, longitude: null };
    } catch (error) {
      console.error('Geocoding error:', error);
      return { latitude: null, longitude: null };
    }
  };

  // Handle File Upload
  const handleFileUpload = (event) => {
    setProgress({
      upload: 0,
      preprocess: 0,
      geocode: 0,
    });
    setProgressMessages({
      upload: '',
      preprocess: '',
      geocode: '',
    });
    const file = event.target.files[0];
    if (file) {
      const fileExtension = file.name.split('.').pop().toLowerCase();

      if (fileExtension === 'json') {
        const reader = new FileReader();
        reader.onload = async (e) => {
          try {
            const data = JSON.parse(e.target.result);
            setProgress((prev) => ({ ...prev, upload: 100 }));
            setProgressMessages((prev) => ({
              ...prev,
              upload: 'Dataset uploaded successfully.',
            }));
            await handlePreprocess(data);
          } catch (error) {
            console.error('Invalid JSON format:', error);
            alert('Error: Uploaded file is not valid JSON.');
          }
        };
        reader.readAsText(file);
      } else if (fileExtension === 'csv') {
        Papa.parse(file, {
          header: true,
          skipEmptyLines: true,
          beforeFirstChunk: (chunk) => {
            // Handle datasets with metadata rows
            const lines = chunk.trim().split('\n');
            const dataStartIndex = lines.findIndex(
              (line) =>
                line.startsWith('Country Name') ||
                line.startsWith('state') ||
                line.startsWith('RowId')
            );
            if (dataStartIndex > 0) {
              return lines.slice(dataStartIndex).join('\n');
            }
            return chunk;
          },
          complete: async (results) => {
            setProgress((prev) => ({ ...prev, upload: 100 }));
            setProgressMessages((prev) => ({
              ...prev,
              upload: 'Dataset uploaded successfully.',
            }));
            await handlePreprocess(results.data);
          },
          error: (error) => {
            console.error('CSV parsing error:', error);
            alert('Error: Failed to parse CSV file.');
          },
        });
      } else {
        alert('Please upload a valid JSON or CSV file.');
      }
    }
  };

  // Monitor Progress Updates
  useEffect(() => {
    if (progress.preprocess === 100) {
      setProgressMessages((prev) => ({
        ...prev,
        preprocess: 'Preprocessing completed.',
      }));
    }
  }, [progress.preprocess]);

  useEffect(() => {
    if (progress.geocode === 100) {
      setProgressMessages((prev) => ({
        ...prev,
        geocode: 'Locations marked on the map.',
      }));
    }
  }, [progress.geocode]);

  // Handle Location Selection from Map
  const handleLocationSelect = useCallback(
    (locationData) => {
      console.log('Marker clicked:', locationData); // Debugging statement
      // Clear state selections if a map marker is selected
      setSelectedState1('');
      setSelectedState2('');
      setCompareMarkersEnabled(false);
      if (selectedLocation && selectedLocation.state === locationData.state) {
        setSelectedLocation(null); // Deselect
        setChartData(null);
      } else {
        setSelectedLocation(locationData);
        setChartData(null);
      }
    },
    [selectedLocation]
  );

  // Handle State Comparison Selection
  const handleStateSelection = () => {
    if (selectedState1 && selectedState2) {
      const data1 = geoData.filter((item) => item.state === selectedState1);
      const data2 = geoData.filter((item) => item.state === selectedState2);

      if (data1.length === 0 || data2.length === 0) {
        alert('One or both selected states have no data.');
        return;
      }

      const aggregatedData1 = data1[0];
      const aggregatedData2 = data2[0];

      // Normalize data for chart display (0.5% to 10%)
      const dataMin = 0; // Minimum data value
      const dataMax = Math.max(
        ...numericHeaders.map((header) =>
          Math.max(aggregatedData1[header], aggregatedData2[header])
        )
      );
      const range = dataMax - dataMin || 1; // Avoid division by zero

      const normalizedData1 = {};
      const normalizedData2 = {};
      const actualData1 = {};
      const actualData2 = {};

      numericHeaders.forEach((header) => {
        const value1 = parseFloat(aggregatedData1[header]);
        const value2 = parseFloat(aggregatedData2[header]);

        actualData1[header] = !isNaN(value1) ? value1 : 0;
        actualData2[header] = !isNaN(value2) ? value2 : 0;

        normalizedData1[header] =
          ((actualData1[header] - dataMin) / range) * 9.5 + 0.5;
        normalizedData2[header] =
          ((actualData2[header] - dataMin) / range) * 9.5 + 0.5;
      });

      // Prepare chart data
      const labels = numericHeaders;
      const dataSet = {
        labels,
        datasets: [
          {
            label: selectedState1,
            data: numericHeaders.map((header) => normalizedData1[header]),
            actualValues: numericHeaders.map((header) => actualData1[header]),
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWidth: 1,
          },
          {
            label: selectedState2,
            data: numericHeaders.map((header) => normalizedData2[header]),
            actualValues: numericHeaders.map((header) => actualData2[header]),
            backgroundColor: 'rgba(54, 162, 235, 0.5)',
            borderColor: 'rgba(54, 162, 235, 1)',
            borderWidth: 1,
          },
        ],
      };

      setChartData(dataSet);
      setSelectedLocation(null); // Deselect any single location

      // Enable the "Mark on Map" button
      setCompareMarkersEnabled(true);
    }
  };

  // Generate Chart Data for Selected Location
  const generateChartData = useCallback(() => {
    if (selectedLocation) {
      const locationValues = numericHeaders.map((header) => ({
        label: header,
        value: parseFloat(selectedLocation[header]) || 0,
      }));

      // Normalize data for chart display (0.5% to 10%)
      const dataMin = 0; // Minimum data value
      const dataMax = Math.max(...locationValues.map((item) => item.value));
      const range = dataMax - dataMin || 1; // Avoid division by zero

      const normalizedValues = locationValues.map((item) => ({
        label: item.label,
        normalizedValue: ((item.value - dataMin) / range) * 9.5 + 0.5,
        actualValue: item.value, // Keep the actual value
      }));

      setChartData({
        labels: normalizedValues.map((item) => item.label),
        datasets: [
          {
            label: selectedLocation.state,
            data: normalizedValues.map((item) => item.normalizedValue),
            actualValues: normalizedValues.map((item) => item.actualValue), // Store actual values
            backgroundColor: [
              'rgba(255, 99, 132, 0.5)',
              'rgba(54, 162, 235, 0.5)',
              'rgba(255, 206, 86, 0.5)',
              'rgba(75, 192, 192, 0.5)',
              'rgba(153, 102, 255, 0.5)',
              'rgba(255, 159, 64, 0.5)',
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
            ],
            borderWidth: 1,
          },
        ],
      });
    }
  }, [selectedLocation, numericHeaders]);

  // Data Generation
  useEffect(() => {
    generateChartData();
  }, [generateChartData, chartType, selectedLocation]);

  // Markers on Map
  const renderMarkers = useCallback(() => {
    if (map && geoData.length > 0) {
      // Remove existing markers
      markers.forEach((marker) => marker.remove());
      const newMarkers = [];

      geoData.forEach((data) => {
        const stateName = data.state;
        const coords = stateCoordinates[stateName];
        if (!coords) return;

        const latitude = parseFloat(coords.latitude);
        const longitude = parseFloat(coords.longitude);

        if (!isNaN(latitude) && !isNaN(longitude)) {
          const el = document.createElement('div');
          el.className = 'marker';
          const isSelected = selectedLocation
            ? selectedLocation.state === data.state
            : false;
          const icon = isSelected ? redMarkerIcon : blueMarkerIcon;
          el.style.backgroundImage = `url(${icon})`;
          el.style.width = '30px';
          el.style.height = '30px';
          el.style.backgroundSize = 'contain';
          el.style.cursor = 'pointer';

          const marker = new mapboxgl.Marker(el)
            .setLngLat([longitude, latitude])
            .addTo(map);

          marker.getElement().addEventListener('click', () => {
            handleLocationSelect(data);
          });

          newMarkers.push(marker);
        }
      });
      setMarkers(newMarkers);
      setMarkersAdded(true);

      setProgress((prev) => ({ ...prev, geocode: 100 }));
      setProgressMessages((prev) => ({
        ...prev,
        geocode: 'Locations marked on the map.',
      }));
    }
  }, [map, geoData, selectedLocation, handleLocationSelect, stateCoordinates]);

  // Render Selected Markers
  const renderSelectedMarkers = useCallback(async () => {
    if (map && selectedState1 && selectedState2) {
      // Remove existing markers
      markers.forEach((marker) => marker.remove());
      const newMarkers = [];

      const selectedStates = [selectedState1, selectedState2];

      const bounds = new mapboxgl.LngLatBounds();

      for (const stateName of selectedStates) {
        const data = geoData.find((item) => item.state === stateName);
        if (!data) continue;
        const coords = stateCoordinates[stateName];
        if (!coords) continue;

        const latitude = parseFloat(coords.latitude);
        const longitude = parseFloat(coords.longitude);

        if (!isNaN(latitude) && !isNaN(longitude)) {
          const el = document.createElement('div');
          el.className = 'marker';
          el.style.backgroundImage = `url(${blueMarkerIcon})`;
          el.style.width = '30px';
          el.style.height = '30px';
          el.style.backgroundSize = 'contain';
          el.style.cursor = 'pointer';

          const marker = new mapboxgl.Marker(el)
            .setLngLat([longitude, latitude])
            .addTo(map);

          // Add a popup or label with the state name
          const popup = new mapboxgl.Popup({ offset: 25 }).setText(stateName);
          marker.setPopup(popup);

          bounds.extend([longitude, latitude]);

          newMarkers.push(marker);
        }
      }

      setMarkers(newMarkers);
      setMarkersAdded(true);

      // Adjust the map to fit the markers
      if (!bounds.isEmpty()) {
        map.fitBounds(bounds, { padding: 50 });
      }
    }
  }, [map, selectedState1, selectedState2, geoData, markers, stateCoordinates]);

  // Re-render markers when selectedLocation changes to update marker colors
  useEffect(() => {
    if (markersAdded) {
      renderMarkers();
    }
  }, [selectedLocation, renderMarkers, markersAdded]);

  // Chart Options
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales:
      chartType === 'Bar'
        ? {
            y: {
              beginAtZero: true,
              min: 0.5,
              max: 10,
            },
          }
        : {},
    plugins: {
      legend: {
        position: 'top',
      },
      datalabels: {
        display: true,
        color: '#000',
        align: 'center', // Align labels to the center of the bars
        anchor: 'center',
        formatter: function (value, context) {
          const dataset = context.chart.data.datasets[context.datasetIndex];
          if (dataset.actualValues) {
            const actualValue = dataset.actualValues[context.dataIndex];
            if (typeof actualValue === 'number' && !isNaN(actualValue)) {
              return actualValue.toFixed(2); // Display actual value
            } else {
              return '';
            }
          } else {
            if (typeof value === 'number' && !isNaN(value)) {
              return value.toFixed(2);
            } else {
              return '';
            }
          }
        },
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            const dataset = context.dataset;
            const actualValue = dataset.actualValues
              ? dataset.actualValues[context.dataIndex]
              : context.parsed.y;
            if (typeof actualValue === 'number' && !isNaN(actualValue)) {
              return `${context.label}: ${actualValue.toFixed(2)}`;
            } else {
              return `${context.label}: ${actualValue || ''}`;
            }
          },
        },
      },
    },
  };

  return (
    <div style={styles.container}>
      {/* Map Container */}
      <div id="map" style={styles.map}></div>

      {/* Sidebar */}
      <div style={styles.sidebar}>
        {/* Combined Controls Box */}
        <div style={styles.combinedBox}>
          {/* Progress Indicators */}
          <div style={styles.progressContainer}>
            <div style={styles.progressItem}>
              <CircularProgressbar
                value={progress.upload}
                text={`${Math.round(progress.upload)}%`}
                styles={{
                  root: { width: '80px' },
                  text: { fontSize: '10px' },
                }}
              />
              <div style={styles.progressMessage}>{progressMessages.upload}</div>
            </div>
            <div style={styles.progressItem}>
              <CircularProgressbar
                value={progress.preprocess}
                text={`${Math.round(progress.preprocess)}%`}
                styles={{
                  root: { width: '80px' },
                  text: { fontSize: '10px' },
                }}
              />
              <div style={styles.progressMessage}>{progressMessages.preprocess}</div>
            </div>
            <div style={styles.progressItem}>
              <CircularProgressbar
                value={progress.geocode}
                text={`${Math.round(progress.geocode)}%`}
                styles={{
                  root: { width: '80px' },
                  text: { fontSize: '10px' },
                }}
              />
              <div style={styles.progressMessage}>{progressMessages.geocode}</div>
            </div>
          </div>

          {/* Upload Data Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Upload Data</h3>
            <input
              type="file"
              accept=".json, .csv"
              onChange={handleFileUpload}
              style={styles.fileInput}
            />
          </div>

          {/* Mark Locations Button */}
          {geoData.length > 0 && !markersAdded && (
            <div style={styles.section}>
              <button
                onClick={renderMarkers}
                style={{
                  ...styles.chartButton,
                  backgroundColor: '#17a2b8',
                  width: '100%',
                }}
              >
                Mark Locations on Map
              </button>
            </div>
          )}

          {/* Dataset Details Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Dataset Details</h3>
            {geoData.length > 0 ? (
              <div style={styles.datasetDetails}>
                <p>
                  <strong>Rows:</strong> {totalRows}
                </p>
                <p>
                  <strong>Columns:</strong> {totalColumns}
                </p>
                <p>
                  <strong>Numeric Columns:</strong> {numericHeaders.join(', ')}
                </p>
                <p>
                  <strong>String Columns:</strong> {stringHeaders.join(', ')}
                </p>
              </div>
            ) : (
              <p>No dataset uploaded yet.</p>
            )}
          </div>

          {/* Location Comparison Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Compare Two States</h3>
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>State 1:</label>
              <select
                value={selectedState1}
                onChange={(e) => setSelectedState1(e.target.value)}
                style={styles.selectDropdown}
                disabled={stateList.length === 0}
              >
                <option value="">Select State</option>
                {stateList.map((state) => (
                  <option key={state} value={state}>
                    {state}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>State 2:</label>
              <select
                value={selectedState2}
                onChange={(e) => setSelectedState2(e.target.value)}
                style={styles.selectDropdown}
                disabled={stateList.length === 0}
              >
                <option value="">Select State</option>
                {stateList.map((state) => (
                  <option key={state} value={state}>
                    {state}
                  </option>
                ))}
              </select>
            </div>
            <button
              onClick={handleStateSelection}
              disabled={!selectedState1 || !selectedState2}
              style={{
                ...styles.chartButton,
                backgroundColor:
                  selectedState1 && selectedState2 ? '#28a745' : '#6c757d',
              }}
            >
              Compare
            </button>

            {/* New Mark on Map Button */}
            {compareMarkersEnabled && (
              <div style={{ marginTop: '10px' }}>
                <button
                  onClick={renderSelectedMarkers}
                  style={{
                    ...styles.chartButton,
                    backgroundColor: '#17a2b8',
                    width: '100%',
                  }}
                >
                  Mark Selected States on Map
                </button>
              </div>
            )}
          </div>

          {/* Chart Type Selection Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Select Chart Type</h3>
            <div style={styles.chartOptions}>
              <button
                onClick={() => setChartType('Bar')}
                style={{
                  ...styles.chartButton,
                  ...(chartType === 'Bar' ? styles.chartButtonActive : {}),
                }}
              >
                Bar Chart
              </button>
              <button
                onClick={() => setChartType('Pie')}
                style={{
                  ...styles.chartButton,
                  ...(chartType === 'Pie' ? styles.chartButtonActive : {}),
                }}
              >
                Pie Chart
              </button>
              <button
                onClick={() => setChartType('Doughnut')}
                style={{
                  ...styles.chartButton,
                  ...(chartType === 'Doughnut' ? styles.chartButtonActive : {}),
                }}
              >
                Doughnut Chart
              </button>
              <button
                onClick={() => setChartType('PolarArea')}
                style={{
                  ...styles.chartButton,
                  ...(chartType === 'PolarArea' ? styles.chartButtonActive : {}),
                }}
              >
                PolarArea Chart
              </button>
            </div>
          </div>
        </div>

        {/* Chart Display Section */}
        {chartData && (
          <div style={styles.chartContainer}>
            <div style={styles.chartWrapper}>
              <div style={styles.chartContent}>
                <h3>{chartType} Chart</h3>
                {chartType === 'Bar' && <Bar data={chartData} options={chartOptions} />}
                {chartType === 'Pie' && <Pie data={chartData} options={chartOptions} />}
                {chartType === 'Doughnut' && (
                  <Doughnut data={chartData} options={chartOptions} />
                )}
                {chartType === 'PolarArea' && (
                  <PolarArea data={chartData} options={chartOptions} />
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MapView;
