// src/components/styles.js

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh',
  },
  map: {
    width: '65%',
    height: '100vh',
  },
  sidebar: {
    width: '35%',
    padding: '20px',
    backgroundColor: '#f8f9fa',
    overflowY: 'auto',
    borderLeft: '1px solid #ddd',
  },
  combinedBox: {
    marginBottom: '20px',
  },
  progressContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    marginBottom: '20px',
  },
  progressItem: {
    marginBottom: '10px',
  },
  progressMessage: {
    marginTop: '5px',
    fontSize: '14px',
    color: '#28a745',
    textAlign: 'center',
  },
  section: {
    marginBottom: '20px',
  },
  sectionTitle: {
    fontSize: '16px',
    fontWeight: 'bold',
    color: '#333',
    marginBottom: '10px',
  },
  fileInput: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  datasetDetails: {
    fontSize: '14px',
    lineHeight: '1.6',
    color: '#555',
  },
  inputGroup: {
    marginBottom: '10px',
  },
  inputLabel: {
    display: 'block',
    fontSize: '14px',
    color: '#333',
    marginBottom: '5px',
  },
  selectDropdown: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '14px',
  },
  chartOptions: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '10px',
  },
  chartButton: {
    flex: '1 1 calc(50% - 10px)',
    padding: '10px',
    borderRadius: '5px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    textAlign: 'center',
    transition: 'background-color 0.3s',
    fontSize: '14px',
  },
  chartButtonActive: {
    backgroundColor: '#0056b3',
  },
  chartContainer: {
    marginTop: '20px',
  },
  chartWrapper: {
    position: 'relative',
    width: '100%',
    height: '400px',
  },
  chartContent: {
    position: 'absolute',
    width: '100%',
    height: '100%',
  },
};

export default styles;
