const styles = {
  mapContainer: "flex-grow h-full",  // Applied to map container in MapView
  sidebar: "w-1/3 p-5 overflow-y-auto bg-gray-100 border-l border-gray-300",
  progressContainer: "flex flex-col gap-2 mb-4",
  progressItem: "mb-2",
  section: "mb-4 p-3 bg-white rounded shadow",
  sectionTitle: "text-sm font-semibold mb-2 text-gray-800",
  fileInput: "w-full p-2 border border-gray-300 rounded",
  selectDropdown: "w-full p-2 border border-gray-300 rounded",
  chartContainer: "mt-6",
  chartWrapper: "relative w-full h-80",
};

export default styles;
