// src/components/styles.js

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
  },
  map: {
    width: '70%',
    height: '100vh',
  },
  sidebar: {
    width: '30%',
    padding: '20px',
    overflowY: 'auto',
    backgroundColor: '#f8f9fa',
  },
  combinedBox: {
    marginBottom: '20px',
  },
  progressContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  progressItem: {
    marginBottom: '10px',
  },
  progressMessage: {
    marginTop: '5px',
    fontSize: '12px',
    textAlign: 'center',
  },
  section: {
    marginBottom: '20px',
  },
  sectionTitle: {
    marginBottom: '10px',
  },
  fileInput: {
    width: '100%',
  },
  datasetDetails: {
    fontSize: '14px',
  },
  inputGroup: {
    marginBottom: '10px',
  },
  inputLabel: {
    display: 'block',
    marginBottom: '5px',
  },
  selectDropdown: {
    width: '100%',
    padding: '5px',
  },
  chartOptions: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '10px',
  },
  chartButton: {
    flex: '1 1 calc(50% - 10px)',
    padding: '10px',
    backgroundColor: '#6c757d',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    textAlign: 'center',
    marginBottom: '10px',
  },
  chartButtonActive: {
    backgroundColor: '#007bff',
  },
  chartContainer: {
    marginTop: '20px',
  },
  chartWrapper: {
    position: 'relative',
    width: '100%',
    height: '400px',
  },
  chartContent: {
    position: 'absolute',
    width: '100%',
    height: '100%',
  },
};

export default styles;
